// üõí Display grocery products
productsContainer.innerHTML = "";

groceryProducts.forEach(product => {
  // Add extra details
  product.sku = `SKU-${product.id}`;
  product.weight = `${(Math.random() * 5 + 0.5).toFixed(2)} kg`;
  product.dimensions = "10x15x20 cm";
  product.warranty = "N/A";
  product.shippingInfo = "Ships in 2-3 business days";
  product.availability = product.stock > 0 ? "In Stock" : "Out of Stock";
  product.returnPolicy = "7-day return policy";
  product.minOrder = 1;

  const card = document.createElement("div");
  card.className = "product";

  card.innerHTML = `
    <img src="${product.thumbnail}" alt="${product.title}" onerror="this.src='https://via.placeholder.com/150?text=No+Image'">
    <h3>${product.title}</h3>
    <p><strong>Brand:</strong> ${product.brand}</p>
    <p><strong>Price:</strong> $${product.price.toFixed(2)}</p>
    <p><strong>Rating:</strong> ${product.rating} ‚≠ê</p>
  `;

  card.addEventListener("click", () => {
    localStorage.setItem("selectedProduct", JSON.stringify(product));
    window.location.href = "details.html";
  });

  productsContainer.appendChild(card);
});